export default {
  props: {
    zones: Array,
    protos: Array
  },
  data: function data() {
    return {
      arps: []
    };
  },
  methods: {
    filterDnat: function filterDnat(s) {
      return s.target !== 'SNAT';
    },
    afterAdd: function afterAdd(sid) {
      this.$uci.set('firewall', sid, 'target', 'DNAT');
    }
  },
  created: function created() {
    var _this = this;

    this.$ubus.call('oui.network', 'arp_table').then(function (r) {
      r.entries.forEach(function (arp) {
        if (arp.macaddr === '00:00:00:00:00:00') return;

        _this.arps.push([arp.ipaddr, "".concat(arp.ipaddr, " (").concat(arp.macaddr, ")")]);
      });
    });
  }
};
