export default {
  data: function data() {
    return {
      form: {
        username: '',
        password: ''
      },
      rules: {
        username: [{
          required: true,
          message: this.$t('This field is required')
        }]
      },
      valid: true
    };
  },
  methods: {
    handleLogin: function handleLogin() {
      var _this = this;

      this.$refs['login'].validate(function (valid) {
        if (valid) {
          _this.$session.login(_this.form.username, _this.form.password).then(function (valid) {
            if (valid) {
              _this.$session.updateACLs().then(function () {
                _this.$router.push('/');
              });

              return;
            }

            _this.valid = false;
          });
        }
      });
    },
    reset: function reset() {
      this.$refs['login'].resetFields();
      this.valid = true;
    }
  },
  created: function created() {
    this.$session.logout();
  }
};
